mapper.select:
SELECT 

FROM account_inquiry_detail a

WHERE a.id = #{id}

mapper.update:
UPDATE account_inquiry_detail SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM account_inquiry_detail a

WHERE a.ordernum = #{ordernum}

mapper.update:
UPDATE account_inquiry_detail SET 	
	state = #{1}
WHERE id = #{0}

mapper.delete:
UPDATE account_inquiry_detail SET 	
	state ="0"
WHERE ordernum=#{ordernum}

mapper.select:
SELECT ,b.name AS 'unit_name' FROM account_inquiry_detail a LEFT JOIN account_para_info b ON a.unit=b.id
WHERE a.ordernum = #{ordernum}
AND materialcode=#{materialcode}
ORDER BY a.materialName
