mapper.select:
SELECT
FROM account_warehouse a WHERE 1 = 1 AND a.`del_flag` = 0
AND a.housename like CONCAT(CONCAT('%',#{whName}),'%')
AND DATE_FORMAT(a.create_date, '%Y-%m-%d') >= #{sTime}
AND DATE_FORMAT(a.create_date, '%Y-%m-%d') <= #{eTime}
ORDER BY a.create_date DESC

SELECT count(*) AS count FROM account_warehouse a WHERE 1 = 1 AND a.`del_flag` = 0
AND a.housename like CONCAT(CONCAT('%',#{whName}),'%')
AND DATE_FORMAT(a.create_date, '%Y-%m-%d') >= #{sTime}
AND DATE_FORMAT(a.create_date, '%Y-%m-%d') <= #{eTime}
